<script lang="ts" setup>
import { ref, onMounted, nextTick } from 'vue'
import { useRoute } from "vue-router";
import { useRouter } from 'vue-router'
import { useLoginModal } from '@/stores/useLoginModal'
import { updatePostStatus, updatePostCreateAt } from '../../api/forum';
import { useAuthStore } from "@/stores/auth";
import { toggleLike, getListLikePost } from '../../api/forum';
const dialogVisible = ref(false)
const isExpanded = ref(false)
const isOverflowing = ref(false)
const contentRef = ref(null)
const auth = useAuthStore();
const router = useRouter()
const route = useRoute();
const loginModal = useLoginModal()
import CreatePostForum from './CreatePostForum.vue';
import { computed } from 'vue';
const topicColor = computed(() => {
    switch (props.topic_id) {
        case 1: return 'green';
        case 2: return 'red';
        case 3: return 'blue';
        default: return '#BF2C24';
    }
});
const props = defineProps({
    STT: Number,
    postId: Number,
    topic_id: Number,
    title: String,
    username: String,
    user_id: Number,
    created_at: String,
    total_likes: Number,
    total_comments: Number,
    topic_title: String,
    content: String,
    hashtag: String,
    isActive: Boolean
})
function gotoPost(postId) {
    router.push({
        name: 'post-detail',   // Tên route bạn đã định nghĩa trong router/index.js
        params: { id: postId }       // Truyền param id
    })
}
function goToProfile(params) {
    router.push({ name: 'user', params: { id: params } })
}
async function likeShare(postId) {
    if (auth.userId) {
        const res = await toggleLike(auth.userId, postId)
        router.push({
            name: 'post-detail',   // Tên route bạn đã định nghĩa trong router/index.js
            params: { id: postId }       // Truyền param id
        })
    }
    else {
        loginModal.open()
    }
}
async function confirmChangre(postId) {
    const res = await updatePostStatus(postId, "draft");
    dialogVisible.value = false
    window.location.reload();
}
async function updateTime(postId) {
    const res = await updatePostCreateAt(postId);
    window.location.reload();
}
onMounted(async () => {
    await nextTick()
    const el = contentRef.value
    if (el.scrollHeight > el.clientHeight) {
        isOverflowing.value = true
    }
})
</script>

