<template>
    <!-- Nút scroll-top -->
    <transition name="fade">
        <button v-if="showButton" @click="scrollToTop" class="scroll-top-btn" title="Lên đầu trang">

            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"
                xmlns:xlink="http://www.w3.org/1999/xlink">
                <rect y="24" width="24" height="24" transform="rotate(-90 0 24)" fill="url(#pattern0_1968_2268)" />
                <defs>
                    <pattern id="pattern0_1968_2268" patternContentUnits="objectBoundingBox" width="1" height="1">
                        <use xlink:href="#image0_1968_2268" transform="scale(0.00195312)" />
                    </pattern>
                    <image id="image0_1968_2268" width="512" height="512" preserveAspectRatio="none"
                        xlink:href="data:image/png;base64," />
                </defs>
            </svg>

        </button>
    </transition>
</template>

<script setup>
import { ref, onMounted, onUnmounted } from 'vue'

const showButton = ref(false)

const handleScroll = () => {
    showButton.value = window.scrollY > 100 // hiện nút khi cuộn >300px
}

const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' })
}

onMounted(() => window.addEventListener('scroll', handleScroll))
onUnmounted(() => window.removeEventListener('scroll', handleScroll))
</script>

<style scoped>
.scroll-top-btn {
    position: fixed;
    bottom: 30px;
    right: 25px;
    background: linear-gradient(to bottom, #FF6114, #E60000);
    /* màu Vue xanh */
    color: white;
    border: none;
    border-radius: 50%;
    width: 55px;
    height: 55px;
    font-size: 20px;
    cursor: pointer;
    box-shadow: 0 3px 8px rgba(0, 0, 0, 0.2);
    transition: all 0.3s;
    z-index: 999;
}

.scroll-top-btn:hover {
    background: linear-gradient(to bottom, #FF6114, #E60000);
    transform: translateY(-3px);
}

/* hiệu ứng hiện/ẩn */
.fade-enter-active,
.fade-leave-active {
    transition: opacity 0.3s;
}

.fade-enter-from,
.fade-leave-to {
    opacity: 0;
}
</style>
